var dissect, dss, ext, lang, ranges, rgs

import kxk from "../../kxk.js"
let kstr = kxk.kstr
let kseg = kxk.kseg

import util from "./util.js"
let inc = util.inc

import kulur from "../kulur.js"

ext = 'kode'

lang = function (l)
{
    return ext = l
}

ranges = function (s)
{
    return kulur.ranges(s,ext)
}

dissect = function (c)
{
    return kulur.dissect(kstr.lines(c),ext)
}
